'use strict';const form=document.querySelector(".typing-area");inputField=form.querySelector(".input-field");sendBtn=form.querySelector("button");chatBox=document.querySelector(".chat-box");form.onsubmit=a=>{a.preventDefault()};sendBtn.onclick=()=>{let a=new XMLHttpRequest;a.open("POST","php/insert-chat.php",!0);a.onload=()=>{a.readyState===XMLHttpRequest.DONE&&200===a.status&&(inputField.value=" ",scrollToBottom())};let b=new FormData(form);a.send(b)};chatBox.onmouseenter=()=>{chatBox.classList.add("active")};
chatBox.onmouseleave=()=>{chatBox.classList.remove("active")};setInterval(()=>{let a=new XMLHttpRequest;a.open("POST","php/get-chat.php",!0);a.onload=()=>{a.readyState===XMLHttpRequest.DONE&&200===a.status&&(chatBox.innerHTML=a.response,chatBox.classList.contains("active")||scrollToBottom())};let b=new FormData(form);a.send(b)},500);function scrollToBottom(){chatBox.scrollTop=chatBox.scrollHeight};